  "$schema": "https://ui.shadcn.com/schema/registry-item.json",
  "name": "border-beam",
  "type": "registry:ui",
  "title": "Border Beam",
  "description": "An interactive border beam component",
  "files": [
    {
      "path": "registry/magicui/border-beam.tsx",
      "content": "import { cn } from "@/lib/utils";

interface BorderBeamProps {
  className?: string;
  size?: number;
  duration?: number;
  borderWidth?: number;
  anchor?: number;
  colorFrom?: string;
  colorTo?: string;
  delay?: number;
}

export default function BorderBeam({
  className,
  size = 200,
  duration = 15,
  anchor = 90,
  borderWidth = 1.5,
  colorFrom = "#ffaa40",
  colorTo = "#9c40ff",
  delay = 0,
}: BorderBeamProps) {
  return (
    <div
      style={{
        "--size": size,
        "--duration": duration + "s",
        "--anchor": anchor + "%",
        "--border-width": borderWidth + "px",
        "--color-from": colorFrom,
        "--color-to": colorTo,
        "--delay": delay + "s",
      } as React.CSSProperties}
      className={cn(
        "absolute inset-[0] rounded-[inherit] [border:calc(var(--border-width)*1px)_solid_transparent]",
        "[background:linear-gradient(to_right,var(--color-from),var(--color-to),var(--color-from))_border-box]",
        "[mask:linear-gradient(#fff_0_0)_padding-box,_linear-gradient(#fff_0_0)]",
        "[mask-composite:xor]",
        "animate-border-beam",
        className
      )}
    />
  );
}",
      "type": "registry:ui",
      "target": "components/magicui/border-beam.tsx"
    }
  ]
}

